import{_ as t,c as l,j as a,a as r,G as o,a4 as n,B as s,o as p}from"./chunks/framework.DbLu_ZlF.js";const f=JSON.parse('{"title":"宋鹏","description":"","frontmatter":{},"headers":[],"relativePath":"baris/lapis-cv-obsidian/template.md","filePath":"baris/lapis-cv-obsidian/template.md","lastUpdated":null}'),h={name:"baris/lapis-cv-obsidian/template.md"};function c(u,e,d,b,m,g){const i=s("ArticleMetadata");return p(),l("div",null,[e[0]||(e[0]=a("h1",{id:"宋鹏",tabindex:"-1"},[r("宋鹏 "),a("a",{class:"header-anchor",href:"#宋鹏","aria-label":'Permalink to "宋鹏"'},"​")],-1)),o(i),e[1]||(e[1]=n('<blockquote><p>19581662630 | <a href="mailto:baris.anran@qq.com" target="_blank" rel="noreferrer">baris.anran@qq.com</a> | 24岁 <img src="https://share.baris.top/picx/WechatIMG179.jpg" alt="avatar"></p></blockquote><h2 id="工作经验" tabindex="-1">工作经验 <a class="header-anchor" href="#工作经验" aria-label="Permalink to &quot;工作经验&quot;">​</a></h2><div style="display:flex;justify-content:space-between;"><h3>软件开发/运维技术员 - </h3> <p style="text-align:right;">2021年08月 - 2023年05月</p></div><ul><li>负责公司三大系统（致远OA、用友NCC、圣兰ERP）的项目实施与后台运维，提供系统培训与操作支持，确保系统平稳运行并实现高效的流程管控。</li><li>进行系统补丁维护、云服务器管理及产品定向开发，保障公司技术架构的持续稳定性和创新发展。</li><li>领导三大系统数据中台（报表）建设与制作，负责数据的整理与分析，确保业务部门能够快速获取关键信息。</li><li>主导MySQL、Oracle、PostgreSQL数据库的部署与性能调优，编写触发器、视图和存储过程，确保数据操作的高效性和稳定性。</li><li>实现定制化报表开发，优化生产报表、财务报表和其他部门所需的管理报表。</li><li>负责Linux与Windows服务器的维护与管理，搭建并优化Nginx、FTP、Redis等服务，确保各项服务的高可用性与数据安全。</li><li>编写Bat、Shell等脚本进行数据备份与恢复操作，提高数据安全性和灾难恢复能力。</li><li>承担三方系统的二次开发任务，成功实现了单点登录（SSO）、API网关、数据同步、OAuth 2.0授权与数据加密传输等技术功能，提升了系统的安全性与集成效率。</li><li>参与构建基于NextJS的公司官网，优化了网站的响应速度和用户体验，提升了品牌形象和在线业务拓展能力。</li><li>主导基于Django与Python构建的知识库系统，提升了团队内部知识共享与管理效率。</li><li>开发并部署了基于Vue3与Spring Cloud的车辆管理系统，整合了车队信息管理、调度与运营数据，提升了公司管理水平。</li><li>使用Python与Selenium构建浏览器自动化项目，提升了日常业务处理的自动化程度。 </li></ul><div style="display:flex;justify-content:space-between;"><h3>全栈软件开发 - </h3> <p style="text-align:right;">2023年05月 - 至今</p></div><ul><li>负责公司金蝶ERP系统的项目实施与后台运维，提供系统操作培训和流程管控。</li><li>管理超过20个客户出货平台的培训操作与流程优化，提升了平台的操作效率和用户体验。</li><li>开发基于Chrome插件和Vue3的自动化数据填写工具，显著提高了客户平台数据输入的效率和准确性。</li><li>负责基于.Net、WinForm和Dev技术构建的货仓管理系统，包括标签打印、出货检验、送货单打印、生产流程管控、生产报表和品质QC报告等功能模块。</li><li>设计并实施了基于.Net Core与WPF技术的生产全流程数字化信息大屏，为生产线提供实时数据监控与分析。</li><li>基于Spring Boot、Vue3和Element Plus开发了全面的人事管理系统，涵盖人员入职、转调离、排班、审批流程、薪酬管理等模块，并进行第三方系统对接和重构。</li><li>开发了基于Vue2与UniApp的人事系统小程序端，提供更便捷的移动端操作方式。</li><li>构建了基于.Net Core API的通用后端接口系统，用于整合和重写第三方系统，包括人事打卡系统、消费系统、门禁系统和车牌识别系统等，提供高效的数据对接。</li><li>实现了基于TCP协议的后端请求系统，支持智能电表、智能机台、智能机器人等项目的集成与管理，并提供定时任务如报表定时推送、跨平台数据库数据互通等功能。</li><li>封装并调用本地分布式AI项目，如SD WebUI绘画项目、DeepSeek-R1聊天项目、Qianwen标签图片识别项目和Auto3D自动建模项目。</li><li>负责SQL Server数据库的部署与维护，编写触发器、存储过程与视图，通过与积木报表联动，生成生产、仓库和人事等报表，优化了数据分析与决策支持。</li></ul><h2 id="专业技能" tabindex="-1">专业技能 <a class="header-anchor" href="#专业技能" aria-label="Permalink to &quot;专业技能&quot;">​</a></h2><ul><li><p>服务器运维:熟悉Windows server、Centos、Ubuntu、Macos等操作系统。能够独立进行使用以及安装维护。</p></li><li><p>Web前端:熟悉HTML、CSS、Javascript。能够熟练使用 Vue框架完成前端开发任务; 拥有web前端相关证书。</p></li><li><p>系统运维相关:致远oa、用友ncc、圣兰erp、金蝶星空、系统运维经验。对人力资源、供应链、财务等系统模块均有业务上的管理经验以 及丰富的系统实施经验。</p></li><li><p>数据库相关:熟悉MySQL、Oracle、pg，sqlserver数据库。能使用SQL语句完成指定的数据库操作。能够独立进行从搭建到备份到恢复 的整套维护操作。</p></li><li><p>数据中台(报表):针对数据库可视化的数据中台分析。能够使用数据中台针对多个跨域数据库进行数据分析 并进行数据推送。通过编写存储过程，视图，api连接积木报表项目。通过Net api+winform实现数据处理和报表界面。</p></li><li><p>图片处理:能熟练应用PS、CDR等设计软件，能够独立完成各类平面设计工作。</p></li><li><p>视频剪辑:有熟练的剪辑和技巧功底。能熟练掌握PR、AE、FCPX、达芬奇、C4D、Maya等软件。</p></li><li><p>深度计算:对CHATGPT、deepseek、AI绘画、AI语音、AI音乐等项目进行过本地镜像部署与模型训练蒸馏。</p></li><li></li></ul><h2 id="个人项目" tabindex="-1">个人项目 <a class="header-anchor" href="#个人项目" aria-label="Permalink to &quot;个人项目&quot;">​</a></h2><h3 id="cherry-net-cherry-nixue-top" tabindex="-1">Cherry-net - <a href="https://github.com/net-p2p/Cherry.Plugins" target="_blank" rel="noreferrer">cherry.nixue.top</a> <a class="header-anchor" href="#cherry-net-cherry-nixue-top" aria-label="Permalink to &quot;Cherry-net - [cherry.nixue.top](https://github.com/net-p2p/Cherry.Plugins)&quot;">​</a></h3><ul><li><strong>net构建</strong>的P2P转发映射工具</li><li>前端使用<strong>Vue3+Vite5+Naiveui+Pinia+Websocket</strong>等技术构建。</li></ul><h2 id="教育经历" tabindex="-1">教育经历 <a class="header-anchor" href="#教育经历" aria-label="Permalink to &quot;教育经历&quot;">​</a></h2><h3 id="上海电子信息大学-专科-软件工程专业-2019-10-2022-06" tabindex="-1">上海电子信息大学 - 专科 - 软件工程专业 (2019.10 - 2022.06) <a class="header-anchor" href="#上海电子信息大学-专科-软件工程专业-2019-10-2022-06" aria-label="Permalink to &quot;上海电子信息大学 - 专科 - 软件工程专业   (2019.10 - 2022.06)&quot;">​</a></h3><p><strong>Github：</strong> <a href="https://github.com/arxb233" target="_blank" rel="noreferrer">github.com/arxb233</a> | <strong>个人博客：</strong> <a href="https://doc.baris.top" target="_blank" rel="noreferrer">doc.baris.top</a></p>',14))])}const x=t(h,[["render",c]]);export{f as __pageData,x as default};
