import{_ as s,c as i,a4 as o,o as r}from"./chunks/framework.CyeRpf1z.js";const c="/wepe/wepe-01.png",n="/wepe/wepe-02.png",d="/wepe/wepe-03.png",m="/wepe/wepe-04.png",g="/wepe/wepe-05.png",u="/wepe/wepe-06.png",w="/wepe/wepe-07.png",h="/wepe/wepe-08.png",y="/wepe/wepe-09.png",_="/wepe/wepe-10.png",b="/wepe/wepe-11.png",k="/wepe/wepe-12.png",f="/wepe/wepe-13.png",z="/wepe/wepe-14.png",v="/wepe/wepe-15.png",x="/wepe/wepe-16.png",P="/wepe/wepe-17.png",E="/wepe/wepe-18.png",F="/wepe/wepe-19.png",q="/wepe/wepe-20.png",S="/wepe/wepe-21.png",U="/wepe/wepe-22.png",I="/wepe/wepe-23.png",G="/wepe/wepe-24.png",T="/wepe/wepe-25.png",O="/wepe/wepe-26.png",B="/wepe/wepe-27.png",C="/wepe/wepe-28.png",D="/wepe/wepe-29.png",M="/wepe/wepe-30.png",N="/wepe/wepe-31.png",W="/wepe/wepe-32.png",A="/wepe/wepe-33.png",t="/wepe/wepe-34.png",R="/wepe/wepe-35.png",V="/wepe/wepe-36.png",X="/wepe/wepe-37.png",H="/wepe/wepe-38.png",L="/wepe/wepe-39.png",$="/wepe/wepe-40.png",J="/wepe/wepe-41.png",K="/wepe/wepe-42.png",j="/wepe/wepe-43.png",p="/wepe/wepe-44.png",l="/wepe/wepe-45.png",a="/wepe/wepe-46.png",Q="/wepe/wepe-47.png",Y="/wepe/wepe-52.png",Z="/wepe/wepe-53.png",tt="/wepe/wepe-54.png",et="/wepe/wepe-48.png",pt="/wepe/wepe-49.png",lt="/wepe/wepe-50.png",at="/wepe/wepe-51.png",st="/wepe/wepe-56.png",it="/wepe/wepe-55.png",ot="/wepe/wepe-57.png",rt="/wepe/wepe-58.png",yt=JSON.parse('{"title":"微PE制作PE启动盘","description":"","frontmatter":{},"headers":[],"relativePath":"yiov/computer/wepe.md","filePath":"yiov/computer/wepe.md","lastUpdated":null}'),ct={name:"yiov/computer/wepe.md"};function nt(dt,e,mt,gt,ut,wt){return r(),i("div",null,e[0]||(e[0]=[o('<h1 id="微pe制作pe启动盘" tabindex="-1">微PE制作PE启动盘 <a class="header-anchor" href="#微pe制作pe启动盘" aria-label="Permalink to &quot;微PE制作PE启动盘&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">(当前界面文档来自yiov.top)</p><ul><li><a href="https://yiov.top/" target="_blank" rel="noreferrer">yiov</a>「伊奥&#39;s 教程集」</li></ul></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>最好用的PE工具箱，没有之一，用于制造PE启动盘，安装/重装系统等，与 <a href="https://www.laomaotao.net/" target="_blank" rel="noreferrer">老毛桃</a>、<a href="https://www.upe.net/" target="_blank" rel="noreferrer">优启通</a> 是同类工具</p><p>官网：<a href="https://www.wepe.com.cn/" target="_blank" rel="noreferrer">https://www.wepe.com.cn/</a></p><p>官方教程：<a href="https://www.wepe.com.cn/ubook/" target="_blank" rel="noreferrer">https://www.wepe.com.cn/ubook/</a></p><p><img src="'+c+'" alt="" loading="lazy"></p><h2 id="准备" tabindex="-1">准备 <a class="header-anchor" href="#准备" aria-label="Permalink to &quot;准备&quot;">​</a></h2><ul><li><p>新U盘一个(至少要8G)</p></li><li><p>下载 <a href="https://dm.weishi.360.cn/home.html" target="_blank" rel="noreferrer">驱动大师</a> 放入U盘「新系统装驱动要用」</p></li><li><p>下载 <a href="https://dzp.lanzouy.com/ifnOv0rvnqmf" target="_blank" rel="noreferrer">装机工具·激活工具包</a> 放入U盘</p></li></ul><h2 id="pe启动盘" tabindex="-1">PE启动盘 <a class="header-anchor" href="#pe启动盘" aria-label="Permalink to &quot;PE启动盘&quot;">​</a></h2><p>在 <a href="https://www.wepe.com.cn/" target="_blank" rel="noreferrer">微PE官网</a> 右上角 <a href="https://www.wepe.com.cn/download.html" target="_blank" rel="noreferrer">下载</a> ，选择合适的版本</p><details class="details custom-block"><summary>下载64位还是32位？</summary><p>我的电脑 - 右键属性 - 系统类型，可以查看</p><p>一般用新版本，除非你是老机器</p></details><p><img src="'+n+'" alt="" loading="lazy"></p><p>打开下载好出程序，选择第一个 <code>安装PE到U盘</code></p><p><img src="'+d+'" alt="" loading="lazy"></p><p>安装方法默认 <code>方案1</code> ，插入准备好的U盘，立刻安装到U盘</p><div class="warning custom-block"><p class="custom-block-title">注意</p><p>安装会自动格式化U盘，请提前备份U盘内的资料</p><ul><li><p>exFAT：默认格式，支持4G以上文件</p></li><li><p>FAT32：适用于XP系统，不支持4G以上文件</p></li><li><p>NTFS：系统有日志，不停的读写比较伤U盘</p></li></ul></div><p><img src="'+m+'" alt="" loading="lazy"></p><p>开始制作，直到完成安装，这样我们的PE启动盘就做好了</p><p><img src="'+g+'" alt="" loading="lazy"></p><p><img src="'+u+'" alt="" loading="lazy"></p><h2 id="系统镜像" tabindex="-1">系统镜像 <a class="header-anchor" href="#系统镜像" aria-label="Permalink to &quot;系统镜像&quot;">​</a></h2><p>Itellyou 官网：<a href="https://next.itellyou.cn" target="_blank" rel="noreferrer">https://next.itellyou.cn</a></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>原老站点是MSDN：<a href="https://msdn.itellyou.cn/" target="_blank" rel="noreferrer">https://msdn.itellyou.cn/</a></p></div><p><img src="'+w+'" alt="" loading="lazy"></p><p>注册登录即可，选择我们要下载的系统</p><blockquote><p>我直接用百度等第三方账号授权登录</p></blockquote><p><img src="'+h+'" alt="" loading="lazy"></p><p>比如 win11 系统，这里有一些简单的信息预览</p><p><img src="'+y+'" alt="" loading="lazy"></p><p>下拉一点，不知道下哪个版本的，就下<code>建议使用</code>，选ed2k用迅雷下载好</p><details class="details custom-block"><summary>版本有什么区别</summary><p>版本无使用区别，VL为团购版，零售版为单卖版</p><ul><li><p>business_editions版，俗称VL版，包含：</p><ul><li><p>Education(教育版)</p></li><li><p>Enterprise(企业版)</p></li><li><p>Professional(专业版)</p></li></ul></li><li><p>consumer_editions版，俗称零售版，包含：</p><ul><li><p>Home(家庭版)</p></li><li><p>Education(教育版)</p></li><li><p>Professional(专业版)</p></li></ul></li></ul></details><details class="details custom-block"><summary>企业版 / 教育版 / 专业版 / 家庭版 对比</summary><ul><li><p>企业版：功能最全，价格最贵；装机比较常见；主要受众 大企业</p></li><li><p>教育版：功能次之，价格较贵；主要受众 学校等教育机构</p></li><li><p>专业版：功能多，价格高；装机比较常见；主要受众 中小企业</p></li><li><p>家庭版：功能少，价格低；主要受众 普通家庭</p></li></ul></details><p><img src="'+_+'" alt="" loading="lazy"></p><p>下载完成后，我们在U盘新建一个文件夹ISO，专门来放系统镜像文件</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>8G一般也就能放两个</p></div><p><img src="'+b+'" alt="" loading="lazy"></p><p>由于下载的是原版纯净系统，是未激活的，我打包好了 <a href="https://dzp.lanzouy.com/ifnOv0rvnqmf" target="_blank" rel="noreferrer">激活工具 和 驱动安装程序</a></p><p>如果有帮到您，请一定支持一下 MSDN 的站长，为爱发电真的很棒！！</p><p><img src="'+k+'" alt="" loading="lazy"></p><h2 id="系统安装" tabindex="-1">系统安装 <a class="header-anchor" href="#系统安装" aria-label="Permalink to &quot;系统安装&quot;">​</a></h2><h3 id="bios设置" tabindex="-1">BIOS设置 <a class="header-anchor" href="#bios设置" aria-label="Permalink to &quot;BIOS设置&quot;">​</a></h3><p>一说到BIOS设置就头疼，不要慌，开机的时候其实已经告诉你了</p><details class="details custom-block"><summary>组装机 主板品牌 启动快捷键</summary><table tabindex="0"><thead><tr><th style="text-align:center;">组装机主板品牌</th><th style="text-align:center;">启动快捷键</th></tr></thead><tbody><tr><td style="text-align:center;">华硕 / 盈通</td><td style="text-align:center;">F8</td></tr><tr><td style="text-align:center;">映泰 / 冠铭</td><td style="text-align:center;">F9</td></tr><tr><td style="text-align:center;">翔升</td><td style="text-align:center;">F10</td></tr><tr><td style="text-align:center;">微星 / 华擎 / 昂达</td><td style="text-align:center;">F11</td></tr><tr><td style="text-align:center;">技嘉 / Intel / 致铭</td><td style="text-align:center;">F12</td></tr><tr><td style="text-align:center;">冠盟 / 顶星</td><td style="text-align:center;">F11或F12</td></tr><tr><td style="text-align:center;">斯巴达克 / 双敏 / 捷波 / 磐英 / 磐正</td><td style="text-align:center;">ESC</td></tr><tr><td style="text-align:center;">杰微</td><td style="text-align:center;">ESC或F8</td></tr><tr><td style="text-align:center;">七彩虹 / 精英 / 铭瑄</td><td style="text-align:center;">ESC或F11</td></tr><tr><td style="text-align:center;">梅捷 / 富士康</td><td style="text-align:center;">ESC或F12</td></tr></tbody></table></details><details class="details custom-block"><summary>笔记本品牌 启动快捷键</summary><table tabindex="0"><thead><tr><th style="text-align:center;">笔记本品牌</th><th style="text-align:center;">启动快捷键</th></tr></thead><tbody><tr><td style="text-align:center;">微星</td><td style="text-align:center;">F11</td></tr><tr><td style="text-align:center;">联想 / Thinkpad / 宏基 / 神舟 / 东芝 / IBM / 富士通 / 海尔 / 方正 / 清华同方 / 技嘉 / Gateway / eMachines</td><td style="text-align:center;">F12</td></tr><tr><td style="text-align:center;">三星</td><td style="text-align:center;">F2或F12</td></tr><tr><td style="text-align:center;">明基</td><td style="text-align:center;">F8或F9</td></tr><tr><td style="text-align:center;">惠普</td><td style="text-align:center;">F9或F12</td></tr><tr><td style="text-align:center;">索尼</td><td style="text-align:center;">ESC</td></tr><tr><td style="text-align:center;">华硕</td><td style="text-align:center;">ESC或F8</td></tr><tr><td style="text-align:center;">戴尔</td><td style="text-align:center;">ESC或F12</td></tr><tr><td style="text-align:center;">苹果</td><td style="text-align:center;">长按“option”键</td></tr></tbody></table></details><p>以我的组装机电脑为例，微星主板重启电脑按 <code>F11</code> 键，进入界面后后松手</p><div class="tip custom-block"><p class="custom-block-title">建议</p><p>直接在启动顺序这里改就好了，重启就恢复了，简单省事</p><p>不用非得去BIOS里设置，设置完你还要去改回来，麻烦一点</p></div><p><img src="'+f+'" alt="" loading="lazy"></p><p>弹窗了用上下键，优先选带UEFI前缀的U盘选项，没有就选不带的，回车</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>一般新机器都是UEFI模式，老机器都是Legacy模式</p></div><p><img src="'+z+'" alt="" loading="lazy"></p><p>Legacy和UEFI模式不一样，进入的界面也不一样</p><p>使用上的区别在后面 <a href="#pe系统工具">PE系统工具：微PE小助手</a> 中查看</p><p><img src="'+v+'" alt="" loading="lazy"></p><p><img src="'+x+'" alt="" loading="lazy"></p><p>等它自动启动进入PE系统，就能看到桌面啦</p><details class="details custom-block"><summary>反反复复都进不去</summary><p>确保启动顺序没问题，就是U盘坏掉了</p></details><p><img src="'+P+'" alt="" loading="lazy"></p><h3 id="pe系统工具" tabindex="-1">PE系统工具 <a class="header-anchor" href="#pe系统工具" aria-label="Permalink to &quot;PE系统工具&quot;">​</a></h3><p>简单的了解一下即可，想深入了解工具使用的请自便</p><details class="details custom-block"><summary>window密码修改器</summary><p>内置了<a href="http://cdslow.org.ru/ntpwedit/" target="_blank" rel="noreferrer">NTPWED</a> ，无需自行下载即可使用</p><p>使用比较简单，点打开输入密码就好了，适用于忘记密码</p><p><img src="'+E+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>dism++</summary><p>内置了 <a href="https://github.com/Chuyu-Team/Dism-Multi-language/releases" target="_blank" rel="noreferrer">Dism++</a>，无需自行下载即可使用</p><p>主要功能：系统垃圾清理/驱动注入</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>当然还可以重装系统/备份还原/清除开机密码/引导修复/等</p></div><p>垃圾清理：空间回收</p><p><img src="'+F+'" alt="" loading="lazy"></p><p>重装系统：工具箱- 系统还原 - 选择镜像，勾选格式化和引导，确定即可</p><p><img src="'+q+'" alt="" loading="lazy"></p><p>驱动注入：驱动管理-添加驱动</p><p>根据主板品牌去官网找驱动即可，或者用 <a href="https://www.intel.cn/content/www/cn/zh/download/17344/22824/intel-usb-3-0-extensible-host-controller-driver-for-intel-8-9-100-series-and-intel-c220-c610-chipset-family.html?" target="_blank" rel="noreferrer">英特尔公版USB 3.0 驱动</a></p><div class="warning custom-block"><p class="custom-block-title">注意</p><p>由于win7原版镜像没有USB 3.0 驱动，系统安装完成不要重启！</p><p>注入3.0驱动后再重启，否则会出现鼠标键盘不能用的情况</p><hr><p>win8以上系统集成了，不用安装，这就是为什么不推荐安装旧系统</p></div><p><img src="'+S+'" alt="" loading="lazy"></p><p>打补丁：更新管理- 添加 - 选择补丁，安装完成即可</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>如果电脑有 NVME 的固态盘，且还是固执的要装win7，那需要打2个补丁</p></div><p>不然识别不了盘符，装机工具包里有的</p><p><img src="'+U+'" alt="" loading="lazy"></p><p>其他功能就自行摸索吧，用的频率就不高了</p></details><details class="details custom-block"><summary>⭐ 微PE小助手 启动方式 [必看]</summary><p>主要是用来查看当前启动的方式， <code>legacy</code> 和 <code>UEFI</code></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>固定组合 <code>legacy+MBR</code> 和 <code>UEFI+GPT</code> 必须是一一对应</p><p>如果你的启动方式是<code>legacy</code> ，那你的硬盘分区就必须是<code>MBR</code></p><p>如果你的启动方式是<code>UEFI</code> ，那你的硬盘分区就必须是<code>GPT</code></p></div><p><img src="'+I+'" alt="" loading="lazy"></p><p>简单科普下插件的这些参数，分区助手也能看到</p><blockquote><p>主要是老方式和新方式，推荐都用新的</p></blockquote><ul><li><p>启动方式：legacy / UEFI</p></li><li><p>硬盘分区：MBR / GPT</p></li><li><p>硬盘工作模式：IDE / AHCI</p></li><li><p>文件系统：FAT32 / NTFS (启动盘格式exFAT)</p></li></ul><p><img src="'+G+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>硬盘分区 新机器 [必看]</summary><p>如果是新安装的硬盘，一定要分区好了，再去装系统！</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>常见的硬盘组合：1个固态硬盘(装系统)+1个机械硬盘(装软件)</p><p>固态盘不分区，机械硬盘分区</p></div><p>分区工具 <a href="https://www.diskgenius.cn/" target="_blank" rel="noreferrer">易数科技的 DiskGenius</a> 和 <a href="https://www.disktool.cn/" target="_blank" rel="noreferrer">傲梅科技的 分区助手</a></p><details class="details custom-block"><summary>工具有什么区别？</summary><p>两个工具使用上是一样的，DiskGenius更强大，分区助手界面更直观</p></details><p><img src="'+T+'" alt="" loading="lazy"></p><p>点 <code>快速分区</code> ，根据系统选 <code>MBR</code> 和 <code>GPT</code> ，主分区就是window系统要安装的盘</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>DiskGenius 和 分区助手 都可以</p></div><p><img src="'+O+'" alt="" loading="lazy"></p><p><img src="'+B+'" alt="" loading="lazy"></p><p>如果你的启动方式是 <code>UEFI</code> ，那么你需要把硬盘分区转换成 <code>GPT</code></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>启动方式和分区是对应的，就不用改</p></div><p><img src="'+C+'" alt="" loading="lazy"></p><p>转换成GPT硬盘 - 选择系统盘 - 提交 - 执行即可</p><p><img src="'+D+'" alt="" loading="lazy"></p><p><img src="'+M+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>备份还原</summary><p>现在已经不太流行用 <code>Ghost备份还原</code> 了，常用的是 <code>CGI备份还原</code></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>Ghost：只能还原第三方GHO，捆绑程序一大堆</p><p>CGI：除了还原第三方GHO，选择镜像文件，还可以直接还原官方原版系统</p></div><p><img src="'+N+'" alt="" loading="lazy"></p><p>CGI还原系统：选择系统盘，再选择U盘里的ISO镜像，执行即可</p><p><img src="'+W+'" alt="" loading="lazy"></p></details><h3 id="开始安装" tabindex="-1">开始安装 <a class="header-anchor" href="#开始安装" aria-label="Permalink to &quot;开始安装&quot;">​</a></h3><p>常见的重装包含3种：CGI备份还原、Winntsetup、直装，我只说后两者，其他的前面也提到过了</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>新硬盘安装的，请先看 <a href="#pe系统工具">PE系统工具 - 硬盘分区</a></p></div><details class="details custom-block"><summary>⭐ 格式化系统盘 [必看]</summary><p>安装前我们先用 <code>DiskGenius</code> 或 <code>分区助手</code> 格式化一下系统盘</p><p><img src="'+A+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>直装（原生安装 适用win8以上）</summary><p>选择我们之前下载好的ISO镜像文件，右键 <code>装载</code> 或者 直接双击</p><p><img src="'+t+'" alt="" loading="lazy"></p><p>这样我们的镜像文件就挂载到驱动器上了，我们直接点 <code>Setup.exe</code> 安装</p><p><img src="'+R+'" alt="" loading="lazy"></p><p><img src="'+V+'" alt="" loading="lazy"></p><p>window安装器打开了，下一步</p><p><img src="'+X+'" alt="" loading="lazy"></p><p>这里我们选择 <code>我没有产品秘钥</code> ，安装好后我们用工具激活</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>除非你就是想花钱 →_→</p></div><p><img src="'+H+'" alt="" loading="lazy"></p><p>这里按需选择系统版本，下一步</p><div class="tip custom-block"><p class="custom-block-title">版本区别</p><ul><li><p>企业版：功能最全，价格最贵；装机比较常见；主要受众 大企业</p></li><li><p>教育版：功能次之，价格较贵；主要受众 学校等教育机构</p></li><li><p>专业版：功能多，价格高；装机比较常见；主要受众 中小企业</p></li><li><p>家庭版：功能少，价格低；主要受众 普通家庭</p></li></ul></div><p><img src="'+L+'" alt="" loading="lazy"></p><p>这里选择自定义，仅安装window</p><p><img src="'+$+'" alt="" loading="lazy"></p><p>之前没有格式化，这里会有老系统残留出来百十来兆的小分区，直接把小分区连同系统大分区一起删除</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>例图中1和2是多出来的，3是系统分区</p></div><p><img src="'+J+'" alt="" loading="lazy"></p><p>一起删除后，形成一个未分配的空间，直接下一步</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>若这里提示选中的磁盘具有MBR分区表，只能安装到GPT磁盘中</p></div><p><img src="'+K+'" alt="" loading="lazy"></p><p>系统开始安装，自动重启后简单设置一下，即可进入桌面</p><p><img src="'+j+'" alt="" loading="lazy"></p><p><img src="'+p+'" alt="" loading="lazy"></p><p><img src="'+l+'" alt="" loading="lazy"></p><p><img src="'+a+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>Windows安装器（WinNTSetup）</summary><p>Winntsetup安装无系统的限制，XP系统选左侧，win7及以上选右侧</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>XP系统时真的老了，但是还没有被放弃</p></div><p><img src="'+Q+'" alt="" loading="lazy"></p><p>同样的安装前，我们先进U盘点击ISO镜像文件，右键 <code>装载</code> 或者 直接双击</p><p><img src="'+t+'" alt="" loading="lazy"></p><p>选择挂载驱动器里的 <code>source</code> 目录下的的 <code>install.wim</code> 文件，选择安装版本，开始安装</p><div class="tip custom-block"><p class="custom-block-title">版本区别</p><ul><li><p>企业版：功能最全，价格最贵；装机比较常见；主要受众 大企业</p></li><li><p>教育版：功能次之，价格较贵；主要受众 学校等教育机构</p></li><li><p>专业版：功能多，价格高；装机比较常见；主要受众 中小企业</p></li><li><p>家庭版：功能少，价格低；主要受众 普通家庭</p></li></ul></div><p><img src="'+Y+'" alt="" loading="lazy"></p><p><img src="'+Z+'" alt="" loading="lazy"></p><p><img src="'+tt+'" alt="" loading="lazy"></p><p>系统开始安装，自动重启后简单设置一下，即可进入桌面</p><p><img src="'+p+'" alt="" loading="lazy"></p><p><img src="'+l+'" alt="" loading="lazy"></p><p><img src="'+a+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>Windows安装器（WinNTSetup）：XP系统安装</summary><p>若无必要，不建议安装这么古老的系统，除非机器带不动</p><p>选择我们之前下载好的ISO镜像文件，右键 <code>装载</code> 或者 直接双击</p><p><img src="'+t+'" alt="" loading="lazy"></p><p>安装位置选我们装载的驱动盘符，其他都默认，安装即可</p><p><strong>注意：XP系统没有USB3.0/win7没有NVME驱动，请装完系统先别重启！！先看 <code>dism++注入驱动</code> 再重启！！！</strong></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>老机器和没有固态盘的不用装，已经重启了就没办法再次注入了，只能重装</p></div><p><img src="'+et+'" alt="" loading="lazy"></p><p>熟悉的界面，启动画面和桌面，曾经王者回来了</p><p><img src="'+pt+'" alt="" loading="lazy"></p><p><img src="'+lt+'" alt="" loading="lazy"></p><p><img src="'+at+'" alt="" loading="lazy"></p></details><h2 id="驱动安装" tabindex="-1">驱动安装 <a class="header-anchor" href="#驱动安装" aria-label="Permalink to &quot;驱动安装&quot;">​</a></h2><p>如今的驱动不付费就限速，恶心啊，我用的是360旗下免费的</p><p>驱动大师：<a href="https://dm.weishi.360.cn/home.html" target="_blank" rel="noreferrer">https://dm.weishi.360.cn/home.html</a></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>轻巧版需有网络在线安装；网卡版无需网络离线安装</p></div><p><img src="'+st+'" alt="" loading="lazy"></p><h2 id="系统激活" tabindex="-1">系统激活 <a class="header-anchor" href="#系统激活" aria-label="Permalink to &quot;系统激活&quot;">​</a></h2><p><a href="https://dzp.lanzouy.com/ifnOv0rvnqmf" target="_blank" rel="noreferrer">系统激活工具包</a> 我已经打包好了，win10+的用 <a href="https://cmwtat.cloudmoe.com/cn.html" target="_blank" rel="noreferrer">云萌</a> 就行</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>建议是提前下到U盘里，等系统安装完成，就直接激活</p></div><p><img src="'+it+'" alt="" loading="lazy"></p><p><img src="'+ot+'" alt="" loading="lazy"></p><p>使用就不说了，就是点点点的事情，非常简单，完结！</p><div class="warning custom-block"><p class="custom-block-title">注意</p><p>云萌仅数字权利激活，Office不可用，可以选择国产的WPS</p><p>要用Office可以选择KMS激活方式，但是180天后需重新激活</p></div><p><img src="'+rt+'" alt="" loading="lazy"></p><h2 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h2><details class="details custom-block"><summary>U盘如何恢复</summary><p>在 <a href="https://rufus.ie/zh/" target="_blank" rel="noreferrer">rufus</a> 官网下载软件并打开，或者使用下面的链接下载</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://d-image.i4.cn/i4web/image/news/2020-07-16/fileTypeImages_1594880598242082789.zip</span></span></code></pre></div><p>插入U盘-引导类型-选择 <code>FreeDOS</code> - 点击开始，直到完成</p></details><h2 id="特别鸣谢" tabindex="-1">特别鸣谢 <a class="header-anchor" href="#特别鸣谢" aria-label="Permalink to &quot;特别鸣谢&quot;">​</a></h2><ul><li><p><a href="https://www.wepe.com.cn/" target="_blank" rel="noreferrer">微PE官网</a></p></li><li><p><a href="https://next.itellyou.cn" target="_blank" rel="noreferrer">Itellyou官网</a>「原MSDN」</p></li><li><p><a href="https://www.diskgenius.cn/" target="_blank" rel="noreferrer">DiskGenius</a>「易数科技」</p></li><li><p><a href="https://www.disktool.cn/" target="_blank" rel="noreferrer">分区助手</a>「傲梅科技」</p></li><li><p><a href="https://dm.weishi.360.cn/home.html" target="_blank" rel="noreferrer">驱动大师</a>「360免费」</p></li><li><p><a href="https://cmwtat.cloudmoe.com/cn.html" target="_blank" rel="noreferrer">云萌</a>「win10+激活工具」</p></li></ul>',89)]))}const _t=s(ct,[["render",nt]]);export{yt as __pageData,_t as default};
