import{_ as e,c as r,a4 as l,o as n}from"./chunks/framework.CyeRpf1z.js";const a="/shell/shell-01.png",y=JSON.parse('{"title":"Shell工具介绍","description":"","frontmatter":{},"headers":[],"relativePath":"yiov/website/shell/index.md","filePath":"yiov/website/shell/index.md","lastUpdated":1734678955000}'),d={name:"yiov/website/shell/index.md"};function i(s,t,o,c,h,g){return n(),r("div",null,t[0]||(t[0]=[l('<h1 id="shell工具介绍" tabindex="-1">Shell工具介绍 <a class="header-anchor" href="#shell工具介绍" aria-label="Permalink to &quot;Shell工具介绍&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">(当前界面文档全部来自yiov.top)</p><ul><li><a href="https://yiov.top/" target="_blank" rel="noreferrer">yiov</a>「伊奥&#39;s 教程集」</li></ul></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><blockquote><p>操作系统：可以分为 <code>kernel</code> (核心) 和 <code>Shell</code> (外壳) 两部分</p></blockquote><p>shell是一个提供操作界面的软件，位于系统外层，用于连接系统内部核心</p><ul><li><p>图形界面shell：Windows系统桌面 <code>explorer.exe</code></p></li><li><p>命令行式shell：Windows系统的 <code>cmd.exe</code></p></li></ul><p><img src="'+a+'" alt="" loading="lazy"></p><h2 id="区别" tabindex="-1">区别 <a class="header-anchor" href="#区别" aria-label="Permalink to &quot;区别&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:center;">shell</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:center;">CMD</td><td style="text-align:left;">是 Command shell 的简写，仅用于执行Windows系统命令和批处理文件</td></tr><tr><td style="text-align:center;">PowerShell</td><td style="text-align:left;">是CMD的升级版，包含CMD所有功能且可跨平台使用</td></tr></tbody></table><h2 id="工具" tabindex="-1">工具 <a class="header-anchor" href="#工具" aria-label="Permalink to &quot;工具&quot;">​</a></h2><blockquote><p>连接服务器：终端(Xshell) → SSH协议 → shell</p><p>带 ⭐ 的是有教程的，点击即可跳转</p></blockquote><table tabindex="0"><thead><tr><th style="text-align:center;">shell</th><th style="text-align:center;">客户端</th><th style="text-align:center;">说明</th></tr></thead><tbody><tr><td style="text-align:center;">CMD</td><td style="text-align:center;">Windows</td><td style="text-align:center;">Command shell 的简写，仅用于执行Windows系统命令和批处理文件</td></tr><tr><td style="text-align:center;"><a href="https://learn.microsoft.com/zh-cn/powershell/" target="_blank" rel="noreferrer">PowerShell</a></td><td style="text-align:center;">Windows</td><td style="text-align:center;">CMD的升级版，包含CMD所有功能且支持脚本环境</td></tr><tr><td style="text-align:center;"><a href="https://www.openssh.com/" target="_blank" rel="noreferrer">OpenSSH</a></td><td style="text-align:center;">Windows、 Mac、Linux</td><td style="text-align:center;">是一款 <a href="https://github.com/openssh" target="_blank" rel="noreferrer">免费开源</a> 的SSH工具，它会加密客户端与服务器之间的流量，遏止窃听、劫持和攻击</td></tr><tr><td style="text-align:center;"><a href="./Xshell.html">⭐Xshell</a></td><td style="text-align:center;">Windows</td><td style="text-align:center;">NetSarang 公司旗下的一个安全终端模拟软件（SSH），教育版可免费使用</td></tr><tr><td style="text-align:center;"><a href="http://www.hostbuf.com/" target="_blank" rel="noreferrer">FinalShell</a></td><td style="text-align:center;">Windows、 Mac、Linux</td><td style="text-align:center;">一款国人开发的SSH客户端工具，支持shell和sftp同屏显示，文件管理等</td></tr><tr><td style="text-align:center;"><a href="https://iterm2.com/" target="_blank" rel="noreferrer">iTerm2</a></td><td style="text-align:center;">Mac</td><td style="text-align:center;">Terminal 的替代品，Mac端专用的简介且强大的终端工具</td></tr><tr><td style="text-align:center;"><a href="https://mobaxterm.mobatek.net/" target="_blank" rel="noreferrer">MobaXterm</a></td><td style="text-align:center;">Windows</td><td style="text-align:center;">带有 X11 服务器、选项卡式 SSH 客户端、网络工具等的 Windows 增强终端，分为免费开源版和收费专业版</td></tr><tr><td style="text-align:center;"><a href="./Termius.html">⭐Termius</a></td><td style="text-align:center;">iOS、Android、Windows、 Mac、Linux</td><td style="text-align:center;">支持多平台的SSH客户端软件，可以实现SSH信息同步、FTP/SFTP连接、主题切换等功能</td></tr><tr><td style="text-align:center;"><a href="./JuiceSSH.html">⭐JuiceSSH</a></td><td style="text-align:center;">Android</td><td style="text-align:center;">安卓端免费的SSH客户端，目前已停止更新</td></tr><tr><td style="text-align:center;"><a href="https://orcaterm.cloud.tencent.com/" target="_blank" rel="noreferrer">orcaterm</a></td><td style="text-align:center;">web</td><td style="text-align:center;">遨驰终端，腾讯旗下的网页端SSH工具</td></tr><tr><td style="text-align:center;"><a href="https://tabby.sh/" target="_blank" rel="noreferrer">tabby</a></td><td style="text-align:center;">Windows、 Mac、Linux</td><td style="text-align:center;">前身是 Terminus，是一个可高度配置的终端模拟器和 SSH 或串口客户端</td></tr><tr><td style="text-align:center;"><a href="https://kingtoolbox.github.io/" target="_blank" rel="noreferrer">WindTerm</a></td><td style="text-align:center;">Windows、 Mac、Linux</td><td style="text-align:center;"><a href="https://github.com/kingToolbox/WindTerm" target="_blank" rel="noreferrer">开源 SSH 连接客户端</a>，支持SSH/Sftp/Shell/Telnet/Serial terminal</td></tr><tr><td style="text-align:center;"><a href="https://electerm.github.io/electerm/" target="_blank" rel="noreferrer">electerm</a></td><td style="text-align:center;">Windows、 Mac、Linux</td><td style="text-align:center;">免费<a href="https://github.com/electerm/electerm" target="_blank" rel="noreferrer">开源的SSH客户端</a>，支持ssh/sftp/telnet/serialport/RDP/VNC</td></tr><tr><td style="text-align:center;"><a href="https://www.vandyke.com/products/securecrt/index.html" target="_blank" rel="noreferrer">SecureCRT</a></td><td style="text-align:center;">iOS、Windows、 Mac、Linux</td><td style="text-align:center;">一款支持SSH的终端仿真程序，同时支持Telnet和rlogin协议</td></tr><tr><td style="text-align:center;"><a href="https://kylinshell.com/" target="_blank" rel="noreferrer">kylinshell</a></td><td style="text-align:center;">Windows</td><td style="text-align:center;">一个简单易用的SSH和SFTP工具，提供免费版和商用版</td></tr><tr><td style="text-align:center;"><a href="https://putty.org/" target="_blank" rel="noreferrer">PuTTY</a></td><td style="text-align:center;">Windows</td><td style="text-align:center;">最初由 Simon Tatham 为 Windows 平台开发的 <a href="https://github.com/github/putty" target="_blank" rel="noreferrer">开源软件</a></td></tr><tr><td style="text-align:center;"><a href="https://mremoteng.org/" target="_blank" rel="noreferrer">mRemoteNG</a></td><td style="text-align:center;">Windows</td><td style="text-align:center;">改进版<a href="https://github.com/mRemoteNG/mRemoteNG" target="_blank" rel="noreferrer">开源 SSH 连接客户端</a>，支持VNC、SSH、Telnet等多种协议，内置多种语言</td></tr></tbody></table>',12)]))}const m=e(d,[["render",i]]);export{y as __pageData,m as default};
